/* Procedure structure for procedure `spCargarEjemplar` */

/*!50003 DROP PROCEDURE IF EXISTS  `spCargarEjemplar` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `spCargarEjemplar`(in idPeliculas int(11),in FormatoEjemplar varchar(8)
, in StockPelicula int(11), in CantidadAgregar int(11))
BEGIN
    declare cont int;
    set cont=0;
    While cont <CantidadAgregar do
	if FormatoEjemplar= "BLU-RAY" THEN 
	INSERT INTO ejemplar(idPeliculas, FormatoEjemplar, PrecioAlquilerEjemplar, EstadoEjemplar, PrecioVentaEjemplar) values 
	(idPeliculas, FormatoEjemplar, 20000, "LIBRE",60000);
	end if;
	if FormatoEjemplar= "DVD" THEN 
	INSERT INTO ejemplar(idPeliculas, FormatoEjemplar, PrecioAlquilerEjemplar, EstadoEjemplar, PrecioVentaEjemplar) VALUES 
	(idPeliculas, FormatoEjemplar, 15000, "LIBRE",45000);
	END IF;
	SET cont= cont+1;
     END WHILE;
     update peliculas set StockPelicula= StockPelicula+CantidadAgregar where idPeliculas= idPeliculas;
	
    END */$$
DELIMITER ;

