     in PaisPelicula varchar(50),  in duracionPelicula varchar(10),  in directorPelicula varchar(50),  in ActoresPelicula text,  in SinopsiPelicula text, in StockPelicula int(11),
     in peliculaImagen longblob , in cantidadDVD int(11), in cantidadBluray int(11))
