    private void jbtnBuscarClienteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtnBuscarClienteActionPerformed
        String dni = this.jtxfClienteDNI.getText();

        if (existeDniCliente(dni)) {
            cliente = clienteDAO.obtenerClientePorDni(dni);
            this.jtxfClienteNombres.setText(cliente.getNombres());
            this.jtxfClienteApellidos.setText(cliente.getApellidos());

            mostrarAlquileresDelCliente();

        } else {
            JOptionPane.showMessageDialog(null, "Cliente no existe!");
        }
    }//GEN-LAST:event_jbtnBuscarClienteActionPerformed

    private void jtblAlquileresPorClienteMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jtblAlquileresPorClienteMouseClicked
        int i = this.jtblAlquileresPorCliente.getSelectedRow();

        calcularDiasDeMora(i);

        this.jtxfAlquilerFechayHora.setText(this.listaAlquileresPorCliente.get(i).getFechaInicioAlquiler() + " " + this.listaAlquileresPorCliente.get(i).getHoraInicioAlquiler());
        this.jtxfAlquilerFechaFin.setText(this.listaAlquileresPorCliente.get(i).getFechaFinAlquiler());
        this.jtxfAlquilerCantidadEjemplaresAlquilados.setText("" + this.listaAlquileresPorCliente.get(i).getCantidadPeliculasAlquiladas());
        this.jtxfAlquilerMontoTotal.setText("" + this.listaAlquileresPorCliente.get(i).getPrecioTotalAlquiler());

        mostrarDetalleAlquilerPorAlquiler(this.listaAlquileresPorCliente.get(i));

    }//GEN-LAST:event_jtblAlquileresPorClienteMouseClicked

