    private void jtblDetalleDevolucionMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jtblDetalleDevolucionMouseClicked
        int i = this.jtblAlquileresPorCliente.getSelectedRow();

        this.jtxfEjemplarCodigo.setText("" + this.jtblDetalleDevolucion.getValueAt(i, 1));
        this.jtxfEjemplarTitulo.setText("" + this.jtblDetalleDevolucion.getValueAt(i, 2));
        this.jtxfEjemplarFormato.setText("" + this.jtblDetalleDevolucion.getValueAt(i, 3));
        this.jtxfEjemplarDeuda.setText("" + this.jtblDetalleDevolucion.getValueAt(i, 5));
    }//GEN-LAST:event_jtblDetalleDevolucionMouseClicked

