    private void btnbuscarClienteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnbuscarClienteActionPerformed
        String CI = this.txtCliente.getText().toString();
        System.out.print(CI);
        if (existeCedulaCliente(CI)) {
            cliente = ObtenerClienteCI(CI);

            this.txtnombrecliente.setText(cliente.getNombre());
            this.txtapellidos.setText(cliente.getApellido());
            this.txttipo.setText(cliente.getTipo());
        } else {
            JOptionPane.showMessageDialog(null, "Cliente no existe, puede registrar sus datos!");
            Cliente vcm = new Cliente(null, rootPaneCheckingEnabled);
            vcm.setVisible(true);
            vcm.setDefaultCloseOperation(2);
        }
        // jbtnRegistrarAlquiler.requestFocus();
        // jbtnRegistrarAlquiler.doClick();
    }//GEN-LAST:event_btnbuscarClienteActionPerformed

