    private void jbtnRegistrarAlquilerActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtnRegistrarAlquilerActionPerformed
        if (this.txtCliente.getText().length() != 0) {

            String fechaInicio = this.jlblAlquilerFechaInicio.getText();
            System.out.println("" + fechaInicio);
            String horaInicio = this.jlblAlquilerHoraInicio.getText();
            System.out.println("" + horaInicio);
            //  String fechaDevolucion = new SimpleDateFormat("dd/MM/yyyy").format(selectorfecha.getDate());
            //   System.out.println("" + fechaDevolucion);
            Integer cantidadEjemplares = Base.convertirCadenaEntero((String) this.jtxfAlquilerCantidadEjemplares.getText());
            System.out.println("" + cantidadEjemplares);
            Double montoTotal = Base.convertirCadenaReal(this.jtxfAlquilerMontoTotal.getText());
            System.out.println("" + montoTotal);
            int Funcionario = Base.convertirCadenaEntero(this.txtcodfun.getText());
            int codigo= Base.convertirCadenaEntero(this.txtfactura.getText());

            alquiler = new Alquiler(fechaInicio, horaInicio, fechaDevolucion, montoTotal, cantidadEjemplares, cliente, Funcionario);
            bd.insertarAlquiler(alquiler);

            alquiler = bd.obtenerAlquiler();
            System.out.println("alquiler-id:" + alquiler.getIdAlquiler());
            System.out.println("alquiler-fecha inicio:" + alquiler.getFechaInicioAlquiler());
            System.out.println("alquiler-hora inicio:" + alquiler.getHoraInicioAlquiler());

            insertarDetalleAlquilerALaBaseDatos();

        } else {
            JOptionPane.showMessageDialog(null, "Cliente no existe, debe ingresar o registrar sus datos!");

        }
    }//GEN-LAST:event_jbtnRegistrarAlquilerActionPerformed

